implementation.constructor:

if (this.hbox.value && this.hbox.value.length)
{
  let nsIFileURL = Components.classes["@mozilla.org/network/io-service;1"]
                         .getService(Components.interfaces.nsIIOService)
                         .newURI(this.hbox.value, null, null)
                         .QueryInterface(Components.interfaces.nsIFileURL);
  this.textbox.setAttribute('value', nsIFileURL.file.leafName);
}

method.body:

const nsIFilePicker = Components.interfaces.nsIFilePicker;
var fp = Components.classes["@mozilla.org/filepicker;1"]
                   .createInstance(nsIFilePicker);

var wildmat = "*.wav; *.ogg; *.mp3; *.aiff";
var label = ".wav, .ogg, .mp3; .aiff";

fp.init(window, "", nsIFilePicker.modeOpen);
fp.appendFilter(label, wildmat);
fp.appendFilters(nsIFilePicker.filterAll);
if (this.hbox.value && this.hbox.value.length)
{
  let nsIURL = Components.classes["@mozilla.org/network/io-service;1"]
                          .getService(Components.interfaces.nsIIOService)
                          .newURI(this.hbox.value, null, null)
                          .QueryInterface(Components.interfaces.nsIURL);
  fp.defaultString = nsIURL.fileName;
  fp.displayDirectory = nsIURL.QueryInterface(Components.interfaces.nsIFileURL)
                              .file
                              .parent;
}
else
{
  Components.utils.import("resource://tonequilla/ToneQuillaPlay.jsm");
  fp.displayDirectory =
    ToneQuillaPlay.soundsDirectory
                  .QueryInterface(Components.interfaces.nsILocalFile);
}

var ret = fp.show();

if (ret == nsIFilePicker.returnOK) {
  let url = fp.fileURL;
  this.hbox.value = url.spec;
  this.textbox.value = fp.file.leafName;
}
